mapper.select:
SELECT id, jiaoyu_type_id, ( SELECT a.NAME FROM type_info a WHERE a.id = c.jiaoyu_type_id ) jiaoyu_type, remark, bumen, bumen_id, user_id, ( SELECT a.NAME FROM sys_user a WHERE a.user_id = c.user_id ) user_name, flag, type, STATUS, create_time, jaoyu_time, DATE_FORMAT( jaoyu_time, "%Y-%m-%d %H:%i:%s" ) startTimeStr FROM jiaoyu_info c
and remark like concat('%',#{remark},'%') and flag = #{flag} and type = #{type} and status = #{status}
order by ${sort} ${order}
order by create_time desc
limit #{offset}, #{limit}

SELECT t1.id, t1.jiaoyu_type_id, t1.remark, t1.bumen, t1.bumen_id, t1.user_id, ( SELECT a.NAME FROM sys_user a WHERE a.user_id = t1.user_id ) user_name, t1.flag, t1.type, t1.STATUS, t1.create_time, t1.jaoyu_time, t2.title, t2.peixun_type_id, ( SELECT a.NAME FROM type_info a WHERE a.id = t2.peixun_type_id ) jiaoyu_type, DATE_FORMAT( peixun_time, "%Y-%m-%d %H:%i:%s" ) startTimeStr FROM jiaoyu_info t1 LEFT JOIN jiaoyu_peixun_info t2 ON t1.jiaoyu_type_id = t2.id
and t1.remark like concat('%',#{remark},'%') and t1.flag = #{flag} and t1.type = #{type} and t1.status = #{status} and t2.title = #{title}
order by ${sort} ${order}
order by create_time desc
limit #{offset}, #{limit}

select count(*) from jiaoyu_info

   and remark like concat('%',#{remark},'%') 

   and flag = #{flag} 
   and type = #{type} 
   and status = #{status}
