mapper.insert:
insert into jiaoyu_peixun_info (id, title, serial_number,
  peixun_type_id, peixun_type, peixun_time,
  remark, flag, type,
  status, create_time, addr
  )
values (#{id,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, #{serialNumber,jdbcType=INTEGER},
  #{peixunTypeId,jdbcType=INTEGER}, #{peixunType,jdbcType=VARCHAR}, #{peixunTime,jdbcType=TIMESTAMP},
  #{remark,jdbcType=VARCHAR}, #{flag,jdbcType=VARCHAR}, #{type,jdbcType=VARCHAR},
  #{status,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{addr,jdbcType=VARCHAR}
  )

mapper.select:
SELECT id, title, serial_number, peixun_type_id, ( SELECT a.NAME FROM type_info a WHERE a.id = c.peixun_type_id ) peixun_type, peixun_time, DATE_FORMAT( peixun_time, "%Y-%m-%d %H:%i:%s" ) startTimeStr, remark, flag, type, STATUS, create_time, addr FROM jiaoyu_peixun_info c
and user_id = #{userId} and title like concat('%',#{title},'%') and flag = #{flag} and type = #{type} and status = #{status}
order by ${sort} ${order}
order by create_time desc
limit #{offset}, #{limit}

select count(*) from jiaoyu_peixun_info

   and user_id = #{userId} 
   and user_name like concat('%',#{userName},'%') 
   and flag = #{flag} 
   and type = #{type} 
   and status = #{status}
