mapper.select:
SELECT c.id, c.title, c.create_time, DATE_FORMAT( start_time, "%Y-%m-%d %H:%i:%s" ) startTimeStr, c.start_time, c.addr, c.user_id, ( SELECT a.NAME FROM sys_user a WHERE a.user_id = c.user_id ) user_name, c.shigu_type_id, ( SELECT a.NAME FROM type_info a WHERE a.id = c.shigu_type_id ) shigu_type, c.remark, c.results, c.STATUS, c.flag, c.type FROM shigu_info c
and c.title like concat('%',#{title},'%') and c.flag = #{flag} and c.type = #{type} and c.status = #{status}
order by ${sort} ${order}
order by create_time desc
limit #{offset}, #{limit}

select count(*) from shigu_info

   and title like concat('%',#{title},'%') 
   and flag = #{flag} 
   and type = #{type} 
   and status = #{status}
