mapper.select:
SELECT id, title, peixun_type_id, ( SELECT a.NAME FROM type_info a WHERE a.id = c.peixun_type_id ) peixun_type, peixun_time, DATE_FORMAT( peixun_time, "%Y-%m-%d %H:%i:%s" ) startTimeStr, user_id, ( SELECT a.NAME FROM sys_user a WHERE a.user_id = c.user_id ) user_name, bumen_id, bumen, flag, STATUS, type, chengji, remark, peixun_num, details, create_time FROM yuangong_peixun_info c
and title like concat('%',#{title},'%') and flag = #{flag} and type = #{type} and status = #{status}
order by ${sort} ${order}
order by create_time desc
limit #{offset}, #{limit}

select count(*) from yuangong_peixun_info

   and name like concat('%',#{name},'%') 
   and flag = #{flag} 
   and type = #{type} 
   and status = #{status}
